import Control.Monad
import Data.List
import Data.Function

text :: String
text = ""

substrings :: [a] -> [[a]]
substrings = inits >=> tails

isPalindrome :: Eq a => [a] -> Bool
isPalindrome s = s == reverse s

longest :: [[a]] -> [a]
longest = maximumBy (compare `on` length)

main :: IO ()
main = print $ longest $ filter isPalindrome $ substrings text